drop table UserGroup;create table UserGroup(  UserGroupID VARCHAR(20) PRIMARY KEY);drop table Role;create table Role(  RoleID VARCHAR(20) PRIMARY KEY,  Description VARCHAR(50),  Enabled TINYINT(1) NOT NULL DEFAULT 1);drop table UserGroupRole;create table UserGroupRole(  UserGroupID VARCHAR(20) NOT NULL REFERENCES UserGroup(UserGroupID) ON UPDATE CASCADE,  RoleID VARCHAR(20) NOT NULL REFERENCES Role(RoleID) ON UPDATE CASCADE,  PRIMARY KEY(UserGroupID,RoleID));drop table User;create table User(	UserID BIGINT(19) AUTO_INCREMENT PRIMARY KEY,	Name VARCHAR(30) NOT NULL,	UserGroupID VARCHAR(20) NOT NULL REFERENCES UserGroup(UserGroupID) ON UPDATE CASCADE,	Username VARCHAR(20) NOT NULL,	Password VARCHAR(256) NOT NULL,  Status TINYINT(4) NOT NULL,	Salt VARCHAR(50) NOT NULL,  Email VARCHAR(50) NOT NULL,  LastLoginDate DATETIME);drop table UserAttribute;create table UserAttribute(  UserID BIGINT(19) PRIMARY KEY REFERENCES User(UserID) ON UPDATE CASCADE);drop table EventLog;create table EventLog(  EventLogID BIGINT(19) AUTO_INCREMENT PRIMARY KEY,  EventDate DATETIME NOT NULL,  UserID BIGINT(19) NOT NULL REFERENCES User(UserID) ON UPDATE CASCADE,  Description VARCHAR(256) NOT NULL);-- SETUP DATAinsert into User values (null,'Tsungai Kaviya','STR_ADMIN','tkaviya',                         '5f4dcc3b5aa765d61d8327deb882cf99', 1,                         'salt', 'tsungai.kaviya@gmail.com', null);insert into UserAttribute values (1);insert into UserGroup values ('STR_USER');insert into UserGroup values ('STR_ADMIN');insert into UserGroupRole values ('STR_USER','ROLE_USER');insert into UserGroupRole values ('STR_ADMIN','ROLE_USER');insert into UserGroupRole values ('STR_ADMIN','ROLE_ADMIN');